import React from 'react'
import Home from './Home'
import Section from './Card'
import Category from './Category'
import ArtificialIntaligence from './ArtificialIntaligence'
import TechNewsReviews from './TechNewsReviews'
import TutorialsGuides from './TutorialsGuides'
import Webdevelopment from './Webdevelopment'
import {
  createBrowserRouter,
  RouterProvider,
  Outlet,
} from 'react-router-dom'

// ✅ Layout wrapper so Category always shows
const Layout = () => (
  <>
    <Category />
    <Outlet />
  </>
)

const Vlog = () => {
  const router = createBrowserRouter([
    {
      element: <Layout />, // ✅ Common layout for all routes
      children: [
        { path: "/", element: <Home /> },                 // root route
        { path: "/All", element: <Home /> },              // alias route
        { path: "/ArtificialIntaligence", element: <ArtificialIntaligence /> },
        { path: "/Webdevelopment", element: <Webdevelopment /> },
        { path: "/TechNewsReviews", element: <TechNewsReviews /> },
        { path: "/TutorialsGuides", element: <TutorialsGuides /> },
      ],
    },
    {
      path: "*",
      element: (
        <div className="flex justify-center items-center h-screen text-3xl font-bold text-red-500">
          404 - Page Not Found
        </div>
      ),
    },
  ])

  return <RouterProvider router={router} />
}

export default Vlog
