import React, { useState } from 'react';
import "react-toastify/dist/ReactToastify.css";
import { toast, ToastContainer } from 'react-toastify';
import { useNavigate } from 'react-router-dom';

const Contactusform = () => {
  const [formData, setFormData] = useState({
    FirstName: "",
    LastName: "",
    Email: "",
    PhoneNumber: "",
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    const { id, value } = e.target;
    setFormData((prev) => ({ ...prev, [id]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const users = JSON.parse(localStorage.getItem("users")) || [];
    users.push(formData);
    localStorage.setItem("users", JSON.stringify(users));

    setFormData({ FirstName: "", LastName: "", Email: "", PhoneNumber: "" });

    toast.success("We will contact you soon");
    setTimeout(() => {
      navigate('/');
    }, 3000);
  };

  return (
    <div className="flex justify-center items-center p-5">
      <section className="w-full max-w-md bg-white bg-opacity-10 rounded-xl shadow-lg p-6">
        <h1 className="text-white text-2xl font-bold text-center">Contact Us</h1>
        <p className="text-white mt-2 text-center">We will get back to you asap!</p>

        <form onSubmit={handleSubmit} className="flex flex-col gap-4 mt-4">
          <input
            className="p-2 w-full rounded-lg border border-gray-400"
            type="text"
            id="FirstName"
            placeholder="First Name"
            value={formData.FirstName}
            onChange={handleChange}
            required
          />
          <input
            className="p-2 w-full rounded-lg border border-gray-400"
            type="text"
            id="LastName"
            placeholder="Last Name"
            value={formData.LastName}
            onChange={handleChange}
            required
          />
          <input
            className="p-2 w-full rounded-lg border border-gray-400"
            type="email"
            id="Email"
            placeholder="Email"
            value={formData.Email}
            onChange={handleChange}
            required
          />
          <input
            className="p-2 w-full rounded-lg border border-gray-400"
            type="text"
            id="PhoneNumber"
            placeholder="Phone Number"
            value={formData.PhoneNumber}
            onChange={handleChange}
            required
          />

          <button
            type="submit"
            className="px-6 py-3 bg-pink-600 hover:bg-pink-700 rounded-lg text-white font-medium shadow-lg w-full"
          >
            Send
          </button>
        </form>
      </section>

      <ToastContainer position="top-center" autoClose={3000} />
    </div>
  );
};

export default Contactusform;
