import React from "react";
import Aboutlist from "./Aboutlist";
const About = () => {
  // Data for "About DevLogix"
  const aboutData = [
    <Aboutlist/>
  ];

  return (
    <div className="flex p-10 bg-purple-500">
      <div>
        {/* Heading */}
        <div className="font-bold ml-12 mt-[20px] text-gray-100 text-5xl">
          About DevLogix
        </div>

        {/* Content Grid */}
        <div className="flex gap-10 justify-center items-center h-[395px]">
          {/* Left Side */}
          <div className="text-gray-200 w-[45%] space-y-4">
            {aboutData.slice(0, 3).map((item) => (
              <div key={item.id}>
                <div className="text-lg font-bold">
                  <span>{item.id}) </span> {item.title}
                </div>
                <div>{item.desc}</div>
              </div>
            ))}
          </div>

          {/* Right Side */}
          <div className="text-white w-[45%] space-y-4">
            {aboutData.slice(3).map((item) => (
              <div key={item.id}>
                <div className="text-lg font-bold">
                  <span>{item.id}) </span> {item.title}
                </div>
                <div>{item.desc}</div>
              </div>
            ))}
          </div>
        </div>

        {/* Button */}
        <button className="ml-20 relative bottom-8 px-6 py-3 bg-pink-600 hover:bg-pink-700 rounded-lg text-white font-medium shadow-lg">
          Read More
        </button>
      </div>
    </div>
  );
};

export default About;
