// src/components/Hamburgernavbar.jsx

import { Link } from 'react-router-dom';
import { useNavigate } from "react-router-dom";

const Hamburgernavbar = ({ user, onLogout }) => {
    const navigate = useNavigate();

    const handleLogout = (e) => {
        e && e.preventDefault();
        if (onLogout) onLogout(null, navigate);
    };

    return (
        <div>
            <nav 
              className="fixed top-0 right-0 flex flex-col 
              items-center bg-white text-black font-bold 
              p-4 shadow-2xl rounded-lg 
              z-[9999]"  // ðŸ‘ˆ forces it above everything
            >
                <ul className="flex flex-col gap-6 md:gap-10 lg:gap-12 lg:justify-end list-none">
                    <li><Link to="/" className="hover:text-yellow-300 hover:underline">Home</Link></li>
                    <li><Link to="/Contact" className="hover:text-yellow-300 hover:underline">Contact</Link></li>
                    <li><Link to="/Blog" className="hover:text-yellow-300 hover:underline">Blog</Link></li>
                    <li><Link to="/Services" className="hover:text-yellow-300 hover:underline">Services</Link></li>
                    <li><Link to="/About" className="hover:text-yellow-300 hover:underline">About</Link></li>
                </ul>

                {/* Auth Buttons */}
                <div className="flex justify-center gap-4 sm:gap-6 sm:text-md lg:gap-10 mt-4">
                    {!user && (
                        <>
                            <Link to="/Login" className="hover:text-yellow-300 hover:underline active:text-red-500">Login</Link>
                            <Link to="/Signup" className="hover:text-yellow-300 hover:underline active:text-red-500">Signup</Link>
                        </>
                    )}
                    {user && (
                        <button
                            className="hover:text-yellow-300 hover:underline active:text-red-500"
                            onClick={handleLogout}
                        >
                            Logout
                        </button>
                    )}
                </div>
            </nav>
        </div>
    );
};

export default Hamburgernavbar;
