import React, { useState } from 'react'
import { toast, ToastContainer } from 'react-toastify'
import "react-toastify/dist/ReactToastify.css";
import Button from './ReadMore'
import { useNavigate, Link } from 'react-router-dom'

const Login = () => {
  const [formData, setFormData] = useState({
    username: "",
    password: "",
  });
  const navigate = useNavigate()

  const handleChange = (e) => {
    const { id, value } = e.target;
    setFormData((prev) => ({ ...prev, [id]: value }));
  }

  const handleLogin = (e) => {
    e.preventDefault();
    const savedUsers = JSON.parse(localStorage.getItem('users')) || []
    const matchedUser = savedUsers.find(
      (user) =>
        user.username === formData.username &&
        user.password === formData.password
    )
    if (matchedUser) {
      toast.success("Logged in successfully")
      setTimeout(() => {
        navigate('/')
      }, 3000)
    } else {
      toast.error("You need to sign up first")
      setTimeout(() => {
        navigate('/Signup')
      }, 3000)
    }
  }

  return (
    <div className='bg-gradient-to-r w-full min-h-screen from-indigo-800 via-purple-800 to-pink-800 p-[104px] justify-center items-center flex'>
      {/* White form box */}
      <div className='rounded-3xl z-10 relative h-[450px] p-5 w-96 text-center bg-white shadow-2xl shadow-purple-400'>
        <h1 className='text-3xl font-bold text-black mt-10'>Log in</h1>
        <form onSubmit={handleLogin} action="post">
          <input
            className='h-10 w-80 bg-white border border-gray-300 rounded-3xl pl-10 text-black mb-6 mt-8'
            type="text"
            value={formData.username}
            onChange={handleChange}
            id='username'
            placeholder='Username'
          />
          <input
            className='h-10 w-80 bg-white border border-gray-300 rounded-3xl pl-10 text-black'
            type="password"
            id="password"
            placeholder="Password"
            value={formData.password}
            onChange={handleChange}
          />
          <div className='mt-7 relative right-[85px] flex items-center'>
            <input type="radio" id='remember' name='remember' value="Remember me" />
            <label className='text-black ml-2' htmlFor="remember">Remember me</label>
          </div>
          <Button
            onClick={handleLogin}
            text='Login'
            textColor='black'
            bgColor='bg-white'
            className='w-56 mt-2'
          />
          <p className='flex pl-12 mt-2 text-black'>
            Don't have any account?
            <Link className='list-none hover:underline ml-1' to="/Signup">
              <li>Sign up</li>
            </Link>
          </p>
        </form>
      </div>
      <ToastContainer position="top-center" autoClose={3000} />
    </div>
  )
}

export default Login
